#!/usr/bin/env bash
set -euo pipefail

MARK="__PAYLOAD__"

embed_payload() {
  local file="$1"

  if [[ ! -f "$file" ]]; then
    echo "❌ Fitxer no trobat: $file" >&2
    exit 1
  fi

  # Demanar clau (2 cops per evitar typos)
  read -s -p "Passphrase per xifrar: " PW
  echo
  read -s -p "Repeteix la passphrase: " PW2
  echo

  if [[ "$PW" != "$PW2" ]]; then
    echo "❌ Les passphrases no coincideixen" >&2
    exit 1
  fi

  # Generar blob xifrat + base64
  local blob
  blob=$(
    openssl enc -aes-256-cbc -salt -pbkdf2 -pass pass:"$PW" -in "$file" \
    | base64 -w0
  )

  # Reconstruir runner: tot fins a MARK, i després afegir el blob nou
  local tmp
  tmp=$(mktemp)

  awk -v mark="$MARK" '
    {
      print $0
      if ($0 == mark) {
        exit
      }
    }
  ' "$0" > "$tmp"

  echo "$blob" >> "$tmp"
  echo >> "$tmp"

  # Mantenir permisos
  chmod --reference="$0" "$tmp" 2>/dev/null || true
  mv "$tmp" "$0"

  echo "✅ Payload actualitzat dins $0 amb el fitxer: $file"
}

run_payload() {
  read -s -p "Passphrase: " PW
  echo

  local payload
  payload=$(
    awk -v mark="$MARK" '
      $0 == mark {flag=1; next}
      flag {print}
    ' "$0"
  )

  if [[ -z "$payload" ]]; then
    echo "❌ No s'ha trobat cap payload xifrat sota $MARK" >&2
    exit 1
  fi

  printf '%s\n' "$payload" \
    | base64 -d \
    | openssl enc -aes-256-cbc -d -salt -pbkdf2 -pass pass:"$PW" \
    | bash -s
}

# ----------------- Entry point -----------------

if [[ $# -ge 1 ]]; then
  # Mode "embed": runner.sh <fitxer>
  embed_payload "$1"
  exit 0
fi

# Mode "run": runner.sh
run_payload
exit 0

__PAYLOAD__


