#!/usr/bin/env bash
set -euo pipefail

MARK="__PAYLOAD__"

ORANGE="\033[38;5;208m"
WHITE_BG="\033[48;5;255m"
RESET="\033[0m"

# Netejar terminal i posar fons blanc
printf "${WHITE_BG}${ORANGE}"
clear

# Calcular centre vertical i horitzontal
rows=$(tput lines)
cols=$(tput cols)

mid_row=$(( rows / 2 ))
# Text del missatge
msg="Waiting for decrypt..."
msg_len=${#msg}
mid_col=$(( (cols - msg_len) / 2 ))

# Moure cursor al centre vertical
tput cup "$mid_row" "$mid_col"
printf "%s" "$msg"

# Tornar a posició final invisible i RESET quan acabi
printf "${RESET}"

# Ara sí: demanar la passphrase **sense mostrar res**
read -s PW
clear

payload=$(awk -v mark="$MARK" '
  $0 == mark {flag=1; next}
  flag {print}
' "$0")

if [[ -z "$payload" ]]; then
  echo "❌ No s'ha trobat cap payload xifrat sota " "$MARK" >&2
  exit 1
fi

printf '%s\n' "$payload" \
  | base64 -d \
  | openssl enc -aes-256-cbc -d -salt -pbkdf2 -pass pass:"$PW" \
  | bash -s

exit 0

__PAYLOAD__

